import react, { useState } from 'react'
import NoteContext from './NoteContext'


const NoteState = (props) => {

  const notesInitial = [];
  const [notes, setNotes] = useState(notesInitial);


  // get all notes
  const getnotes = async() => {
    // API call
    const response = await fetch("http://localhost:5005/api/notes/fetchallnotes ", {
      method: "GET", 
      headers: { 
        "auth-token":"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjRjYTg5OWJjNGVlMzAzODFhOGEyMDdjIn0sImlhdCI6MTY5MTEzNzE4MX0.DMfKNi1vGNmxaT5cjEb7o4oyVegph146JKm9nu-rN8w",
        // 'Content-Type': 'application/x-www-form-urlencoded',
      }
    });
    const json=await response.json();
    console.log(json )
    setNotes(json);
  }


  // add a note
  const addNote = async(title,description,tag) => {
    // TODO API call

    const response = await fetch("http://localhost:5005/api/notes/addnote", {
      method: "POST", 
      headers: { 
        "auth-token":"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjRjYTg5OWJjNGVlMzAzODFhOGEyMDdjIn0sImlhdCI6MTY5MTEzNzE4MX0.DMfKNi1vGNmxaT5cjEb7o4oyVegph146JKm9nu-rN8w",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({title,description,tag})
    });
    const note=response.json();
    // console.log(note)
    // console.log("note added such");
    // // console.log(not.title);
    // // console.log(not.desc);
    // const note = {
    //   "_id": "64d11cf7903e3a72ba6283c8",
    //   "user": "64ca899bc4ee30381a8a207c",
    //   "title": title,
    //   "description": description,
    //   "tag": tag,
    //   "date": "2023-08-07T16:33:59.905Z",
    //   "__v": 0
    // }
    // console.log(note)
    // concat hota add karna ka liya l
    setNotes(notes.concat(note))
  }

  //  delete note
  const deleteNote = async (id) => {
    // api call
    const response = await fetch(`http://localhost:5005/api/notes/deletenote/${id}`, {
      method: "DELETE", 
      headers: {
        "auth-token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjRjYTg5OWJjNGVlMzAzODFhOGEyMDdjIn0sImlhdCI6MTY5MTEzNzE4MX0.DMfKNi1vGNmxaT5cjEb7o4oyVegph146JKm9nu-rN8w",
        "Content-Type": "application/json",
        // 'Content-Type': 'application/x-www-form-urlencoded',
      },
    });
    const json = response.json();
    const newNotes = notes.filter((note) => { return note._id !== id });
    setNotes(newNotes);
    console.log("DElete npte" + id)
  }

  // update note
  const editNote = async (id, title, description, tag) => {
    // API call 

    const response = await fetch(`http://localhost:5005/api/notes/updatenote/${id}`, {
      method: "POST", 
      headers: {
        "auth-token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjRjYTg5OWJjNGVlMzAzODFhOGEyMDdjIn0sImlhdCI6MTY5MTEzNzE4MX0.DMfKNi1vGNmxaT5cjEb7o4oyVegph146JKm9nu-rN8w",
        "Content-Type": "application/json",
        // 'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: JSON.stringify({title,description,tag})
    });
    const json = response.json();

    //Logic to edit ntoe in client 
    for (let index = 0; index < notes.length; index++) {
      const element = notes[index];
      if (element._id === id) {
        element.title = title;
        element.description = description;
        element.tag = tag;
      }
    }
  }


  return (
    <NoteContext.Provider value={{ notes, addNote, deleteNote, editNote,getnotes }}>
      {props.children}
    </NoteContext.Provider>
  )
}




export default NoteState;
