 import React, { useContext, useState } from 'react'
import NoteContext from '../context/notes/NoteContext';

 const AddNote = () => {
  const context = useContext(NoteContext);
  const {addNote } = context;
  const [note,setNote]=useState({
    "title":"",
    "description":"",
    "tag":"default",
  });
    const handleClick=(e)=>{
        e.preventDefault();
         addNote(note.title,note.description,note.tag);
    }
    const onChange=(e)=>{
        // spread operator ka use ho rahe ha aur  
         setNote({...note,[e.target.name]:e.target.value})
    }
   return (
     <div>
      <h2>Add a note</h2>
      <form>
        <div className="mb-3">
          <label htmlFor="title" className="form-label">Title</label>
          <input type="text" className="form-control" id="title" name='title ' aria-describedby="emailH elp"onChange={onChange}  />
        </div>
        <div className="mb-3">
          <label htmlFor="desc" className="form-label">Description</label>
          <input type="text" className="form-control" id="desc"  name='desc' onChange={onChange} />
        </div>
        <div className="mb-3 form-check">
          <input type="checkbox" className="form-check-input" id="exampleCheck1" />
          <label className="form-check-label" htmlFor="exampleCheck1">Check me out</label>
        </div>
        <button type="submit" className="btn btn-primary" onClick={handleClick}>Submit</button>
      </form>

     </div>
   )
 }

 export default AddNote
