import React, { useState } from 'react'
import AlertContext from './AlertContext'
const AlertState = () => {

    cosnt[type, setType] = useState(null);
    cosnt[msg, setMsg] = useState(null);
    const show = (text, message) => {
        window.scroll(0, 0);
        setType(text);
        setMsg(message);
        setTimeout(() => {
            clear();
        }, 3000);
    }
    const clear = () => {
        setType(null);
        setMsg(null);
    }

return (
    <AlertContext.Provider value={{show,clear,type,msg}}>
        {props.children}
    </AlertContext.Provider>
)
}

export default AlertState
